import React from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import Register from "./components/Register";
import Login from "./components/Login";
import Profile from "./components/Profile";
import ProtectedRoute from "./components/ProtectedRoute";
import Dashboard from "./pages/Dashboard";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  return (
    <BrowserRouter>
      {/* Navbar */}
      <nav className="bg-[#0c1427] px-6 py-4 flex justify-between items-center">
        <span className="text-2xl font-bold text-orange-500">JWT Auth Demo</span>
        <div className="flex gap-6 text-white font-semibold">
          <Link to="/profile" className="hover:text-orange-400">
            Account
          </Link>
          <Link to="/login" className="hover:text-orange-400">
            Signin
          </Link>
          <Link to="/register" className="hover:text-orange-400">
            Signup
          </Link>
        </div>
      </nav>

      {/* Landing Page */}
      <div className="min-h-[calc(100vh-4rem)] bg-[#0c1427] flex flex-col justify-center items-center text-center">
        <h1 className="text-6xl font-extrabold text-orange-500 mb-4">
          JWT Auth Demo
        </h1>
        <p className="text-lg text-white font-semibold">
          Stay Authenticated, Stay Secure
        </p>
      </div>

      {/* Routes */}
      <Routes>
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />
        <Route
          path="/profile"
          element={
            <ProtectedRoute>
              <Profile />
            </ProtectedRoute>
          }
        />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>

      {/* Toast notifications */}
      <ToastContainer position="top-right" autoClose={3000} />
    </BrowserRouter>
  );
}

export default App;
