import React, { useState, useEffect, useRef, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api";
import { toast } from "react-toastify";

function Dashboard() {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [accessToken, setAccessToken] = useState("");
  const [timeRemaining, setTimeRemaining] = useState("");

  const globalInterval = useRef(null);
  const activityTimeout = useRef(null);
  const inactivityInterval = useRef(null);

  const navigate = useNavigate();

  // ✅ Countdown timer for session expiry
  const startGlobalCountdown = useCallback(
    (expiresAt) => {
      clearInterval(globalInterval.current);

      const updateCountdown = () => {
        const now = new Date();
        const diff = expiresAt - now;

        if (diff <= 0) {
          setTimeRemaining("Session expired");
          clearInterval(globalInterval.current);
          toast.error("Session expired. Please login again.");
          navigate("/login");
        } else {
          const totalSeconds = Math.floor(diff / 1000);
          const minutes = Math.floor(totalSeconds / 60);
          const seconds = totalSeconds % 60;

          setTimeRemaining(`${minutes}m ${seconds < 10 ? "0" : ""}${seconds}s`);

          // ⚠️ Warning toast at 5 seconds left
          if (totalSeconds === 5) {
            toast.error("⚠️ Session expiring in 5 seconds!");
          }
        }
      };

      updateCountdown(); // run once immediately
      globalInterval.current = setInterval(updateCountdown, 1000);
    },
    [navigate]
  );

  // ✅ Fetch user data and start countdown
  const fetchUserData = async () => {
    try {
      const res = await api.get("/auth/me", { withCredentials: true });
      setUserData(res.data);
      setLoading(false);

      const token = localStorage.getItem("accessToken") || "";
      setAccessToken(token);

      const backendExpires = new Date(res.data.session.expiresAt);
      const storedExpires = sessionStorage.getItem("expiresAt");
      const expiresAt = storedExpires ? new Date(storedExpires) : backendExpires;

      startGlobalCountdown(expiresAt);
    } catch (err) {
      setError("Failed to fetch user data. Please login again.");
      setLoading(false);
    }
  };

  // ✅ Run once on mount
  useEffect(() => {
    fetchUserData();

    return () => {
      clearInterval(globalInterval.current);
      clearTimeout(activityTimeout.current);
      clearInterval(inactivityInterval.current);
    };
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="text-red-500">{error}</p>;

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-2xl font-bold mb-6">Welcome, {userData?.name}!</h1>

      {/* User Info */}
      <div className="bg-white p-4 rounded-lg shadow mb-4">
        <h2 className="text-lg font-semibold">Your Info</h2>
        <p><strong>Username:</strong> {userData?.name}</p>
        <p><strong>Email:</strong> {userData?.email}</p>
      </div>

      {/* Session Info */}
      <div className="bg-white p-4 rounded-lg shadow mb-4">
        <h2 className="text-lg font-semibold">Session Info</h2>
        <p><strong>Session ID:</strong> {userData?.session?.id}</p>
        <p><strong>Created At:</strong> {new Date(userData?.session?.createdAt).toLocaleString()}</p>
        <p><strong>Expires At:</strong> {new Date(userData?.session?.expiresAt).toLocaleString()}</p>
        <p><strong>Max Age:</strong> {Math.floor(userData?.session?.maxAge / 60000)} minutes</p>
        <p><strong>Time Remaining:</strong> {timeRemaining}</p>
      </div>

      {/* Token Info */}
      <div className="bg-white p-4 rounded-lg shadow">
        <h2 className="text-lg font-semibold">Session Token</h2>
        <p>This token expires in approximately {Math.floor(userData?.session?.maxAge / 60000)} minutes from issue.</p>
        <div className="mt-2 p-2 bg-gray-900 text-green-400 rounded font-mono break-all text-xs">
          {accessToken || "Not available"}
        </div>
        <p className="mt-2"><strong>Countdown:</strong> {timeRemaining}</p>
      </div>
    </div>
  );
}

export default Dashboard;
