import React, { useEffect, useState } from "react";
import api from "../api";

export default function Profile() {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({ name: "", email: "" });

  useEffect(() => {
    api
      .get("/api/profile")
      .then((res) => {
        setProfile(res.data);
        setFormData({ name: res.data.name, email: res.data.email });
      })
      .catch((err) => {
        console.error("Error fetching profile:", err);
        setError("You must login first to view your profile.");
      });
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      const res = await api.put("/api/profile", formData); // <-- assumes backend update route
      setProfile(res.data);
      setIsEditing(false);
    } catch (err) {
      console.error("Error updating profile:", err);
      alert("Failed to update profile.");
    }
  };

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="bg-white p-8 rounded-2xl shadow-lg text-center max-w-md">
          <h2 className="text-2xl font-bold text-red-600 mb-2">⚠️ Access Denied</h2>
          <p className="text-gray-700">{error}</p>
          <a
            href="/login"
            className="inline-block mt-4 px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition duration-200"
          >
            Go to Login
          </a>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <p className="text-gray-600 text-lg">Loading profile...</p>
      </div>
    );
  }

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md">
        {!isEditing ? (
          <>
            {/* Avatar */}
            <div className="flex flex-col items-center">
              <div className="w-24 h-24 bg-blue-500 text-white flex items-center justify-center rounded-full text-3xl font-bold mb-4">
                {profile.name ? profile.name[0].toUpperCase() : "U"}
              </div>
              <h2 className="text-2xl font-bold text-gray-800 mb-1">
                {profile.name || "Unknown User"}
              </h2>
              <p className="text-gray-600 mb-4">{profile.email || "No email"}</p>
            </div>

            <div className="border-t pt-4 space-y-2">
              <p>
                <span className="font-medium">Joined:</span>{" "}
                {profile.joined || "N/A"}
              </p>
              <p>
                <span className="font-medium">Role:</span>{" "}
                {profile.role || "User"}
              </p>
            </div>

            {/* Edit Button */}
            <button
              onClick={() => setIsEditing(true)}
              className="mt-6 w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition duration-200"
            >
              Edit Profile
            </button>
          </>
        ) : (
          <>
            <h2 className="text-xl font-bold text-gray-800 mb-4 text-center">
              Edit Profile
            </h2>
            <div className="space-y-4">
              <div>
                <label className="block text-gray-700 font-medium">Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className="mt-1 w-full p-2 border rounded-lg focus:ring focus:ring-blue-300"
                />
              </div>
              <div>
                <label className="block text-gray-700 font-medium">Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="mt-1 w-full p-2 border rounded-lg focus:ring focus:ring-blue-300"
                />
              </div>
            </div>

            <div className="flex gap-3 mt-6">
              <button
                onClick={handleSave}
                className="flex-1 bg-green-600 text-white py-2 rounded-lg hover:bg-green-700 transition duration-200"
              >
                Save
              </button>
              <button
                onClick={() => setIsEditing(false)}
                className="flex-1 bg-gray-400 text-white py-2 rounded-lg hover:bg-gray-500 transition duration-200"
              >
                Cancel
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
