import React from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import Register from "./components/Register";
import Login from "./components/Login";
import Profile from "./components/Profile";
import ProtectedRoute from "./components/ProtectedRoute";
import Dashboard from "./pages/Dashboard";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function Home() {
  return (
    <div className="min-h-[calc(100vh-4rem)] flex flex-col justify-center items-center text-center">
      <h1 className="text-6xl font-extrabold text-orange-500 mb-4">JWT Auth Demo</h1>
      <p className="text-lg text-white/90 font-semibold">Stay Authenticated, Stay Secure</p>
    </div>
  );
}

function App() {
  return (
    <BrowserRouter>
      <div className="min-h-screen bg-gradient-to-br from-[#0c1427] via-[#101a34] to-[#0c1427]">
      {/* Navbar */}
      <nav className="px-6 py-4 flex justify-between items-center bg-white/10 backdrop-blur-md border-b border-white/10">
        {/* Title */}
        <span className="text-2xl font-bold text-orange-400 tracking-tight">
          JWT Auth Demo
        </span>

        {/* Navigation Links */}
        <div className="flex gap-6 text-white font-semibold">
          <Link to="/register" className="hover:text-orange-300">
            Register
          </Link>
          <Link to="/login" className="hover:text-orange-300">
            Login
          </Link>
          <Link to="/profile" className="hover:text-orange-300">
            Profile
          </Link>
          <Link to="/dashboard" className="hover:text-orange-300">
            Dashboard
          </Link>
        </div>
      </nav>

      {/* Routes */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />
        <Route
          path="/profile"
          element={
            <ProtectedRoute>
              <Profile />
            </ProtectedRoute>
          }
        />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>

      {/* Toast notifications */}
      <ToastContainer position="top-right" autoClose={3000} />
      </div>
    </BrowserRouter>
  );
}

export default App;
