import { useEffect, useRef, useState, useCallback } from "react";
import api from "../api";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [timeRemaining, setTimeRemaining] = useState("");
  const [logs, setLogs] = useState([]);
  const [inactivityTimeLeft, setInactivityTimeLeft] = useState(0);
  const [accessToken, setAccessToken] = useState("");

  const inactivityInterval = useRef(null);
  const activityTimeout = useRef(null);
  const globalInterval = useRef(null);

  const INACTIVITY_LIMIT = 3 * 60 * 1000;

  // ✅ Countdown timer for session expiry
  const startGlobalCountdown = useCallback(
    (expiresAt) => {
      clearInterval(globalInterval.current);

      const updateCountdown = () => {
        const now = new Date();
        const diff = expiresAt - now;

        if (diff <= 0) {
          setTimeRemaining("Session expired");
          clearInterval(globalInterval.current);
          toast.error("Session expired. Please login again.");
          navigate("/login");
        } else {
          const totalSeconds = Math.floor(diff / 1000);
          const minutes = Math.floor(totalSeconds / 60);
          const seconds = totalSeconds % 60;

          setTimeRemaining(`${minutes}m ${seconds < 10 ? "0" : ""}${seconds}s`);

          if (totalSeconds === 5) {
            toast.error("⚠️ Session expiring in 5 seconds!");
          }
        }
      };

      updateCountdown(); // run once immediately
      globalInterval.current = setInterval(updateCountdown, 1000);
    },
    [navigate]
  );

  const fetchUserData = async () => {
    try {
      const res = await api.get("/auth/me", { withCredentials: true });
      setUserData(res.data);
      setLoading(false);

      const token = localStorage.getItem("accessToken") || "";
      setAccessToken(token);

      const backendExpires = new Date(res.data.session.expiresAt);
      const storedExpires = sessionStorage.getItem("expiresAt");
      const expiresAt = storedExpires ? new Date(storedExpires) : backendExpires;

      startGlobalCountdown(expiresAt);
    } catch (err) {
      setError("Failed to fetch user data. Please login again.");
      setLoading(false);
    }
  };

  const fetchLogs = async () => {
    try {
      const res = await api.get("/auth/logs", { withCredentials: true });
      setLogs(res.data.logs);
    } catch (err) {
      console.error("Failed to fetch logs:", err);
    }
  };

  const handleLogout = async () => {
    try {
      await api.post("/auth/logout", {}, { withCredentials: true });
      toast.success("Logged out");
      navigate("/login");
    } catch (err) {
      toast.error("Error logging out");
    }
  };

  const resetInactivityTimer = useCallback(() => {
    clearTimeout(activityTimeout.current);
    clearInterval(inactivityInterval.current);
    setInactivityTimeLeft(0);

    activityTimeout.current = setTimeout(() => {
      let timeLeft = INACTIVITY_LIMIT / 1000;
      setInactivityTimeLeft(timeLeft);

      inactivityInterval.current = setInterval(() => {
        timeLeft -= 1;
        setInactivityTimeLeft(timeLeft);
        if (timeLeft <= 0) {
          clearInterval(inactivityInterval.current);
          toast.error("Session expired due to inactivity. Please login again.");
          navigate("/login");
        }
      }, 1000);
    }, INACTIVITY_LIMIT);
  }, [navigate]);

  useEffect(() => {
    fetchUserData();
  }, []);

  useEffect(() => {
    if (userData) fetchLogs();
  }, [userData]);

  useEffect(() => {
    const events = ["mousemove", "keydown", "scroll", "touchstart"];
    events.forEach((event) =>
      window.addEventListener(event, resetInactivityTimer)
    );

    resetInactivityTimer();

    return () => {
      events.forEach((event) =>
        window.removeEventListener(event, resetInactivityTimer)
      );
      clearTimeout(activityTimeout.current);
      clearInterval(inactivityInterval.current);
      clearInterval(globalInterval.current);
    };
  }, [userData, resetInactivityTimer]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-100">
        <p className="text-xl font-semibold text-gray-700">Loading...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
        <p className="text-red-500 text-lg font-medium mb-4">{error}</p>
        <button
          onClick={() => navigate("/login")}
          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          Go to Login
        </button>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center py-10 px-6">
      <h1 className="text-3xl font-bold mb-8 text-gray-800">
        Welcome, {userData.user.username}!
      </h1>

      {/* User Info */}
      <div className="bg-white shadow rounded-lg p-6 w-full max-w-4xl mb-6">
        <h2 className="text-xl font-semibold mb-4 flex items-center">
          <span className="mr-2">👤</span> Your Info
        </h2>
        <div className="flex justify-between">
          <p>
            <span className="font-medium">Username:</span>{" "}
            {userData.user.username}
          </p>
          <p>
            <span className="font-medium">Email:</span> {userData.user.email}
          </p>
        </div>
      </div>

      {/* Session Info */}
      <div className="bg-white shadow rounded-lg p-6 w-full max-w-4xl mb-6">
        <h2 className="text-xl font-semibold mb-4 flex items-center">
          <span className="mr-2">📄</span> Session Info
        </h2>
        <div className="grid grid-cols-2 gap-4">
          <p>
            <span className="font-medium">Session ID:</span>{" "}
            {userData.session.id}
          </p>
          <p>
            <span className="font-medium">Created At:</span>{" "}
            {new Date(userData.session.createdAt).toLocaleString()}
          </p>
          <p>
            <span className="font-medium">Expires At:</span>{" "}
            {new Date(userData.session.expiresAt).toLocaleString()}
          </p>
          <p>
            <span className="font-medium">Max Age:</span>{" "}
            {Math.floor(userData.session.maxAge / 1000 / 60)} minutes
          </p>
          <p>
            <span className="font-medium">Time Remaining:</span>{" "}
            {timeRemaining}
          </p>
        </div>
      </div>

      {/* Session Token */}
      <div className="bg-white shadow rounded-lg p-6 w-full max-w-4xl mb-6">
        <h2 className="text-xl font-semibold mb-4 flex items-center">
          <span className="mr-2">🔐</span> Session Token
        </h2>
        <div className="space-y-2">
          <div className="text-sm text-gray-600">
            This token expires in approximately 3 minutes from issue.
          </div>
          <div className="p-3 bg-gray-900 text-gray-100 rounded font-mono text-xs break-all">
            {accessToken || "No token available"}
          </div>
          <div className="text-sm">
            <span className="font-medium">Countdown:</span> {timeRemaining}
          </div>
        </div>
      </div>

      {/* Logs Section ✅ Restored */}
      <div className="bg-white shadow rounded-lg p-6 w-full max-w-4xl mb-6">
        <h2 className="text-xl font-semibold mb-4 flex items-center">
          <span className="mr-2">📜</span> User Activity Logs
        </h2>
        {logs.length > 0 ? (
          <ul className="list-disc list-inside text-gray-700 space-y-1">
            {logs.map((log, idx) => (
              <li key={idx}>
                {log.message} –{" "}
                <span className="text-gray-500 text-sm">
                  {new Date(log.timestamp).toLocaleString()}
                </span>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-500">No activity logs available.</p>
        )}
      </div>

      {/* Logout */}
      <button
        onClick={handleLogout}
        className="px-6 py-2 bg-red-500 text-white font-semibold rounded-lg shadow hover:bg-red-600"
      >
        Logout
      </button>

      {inactivityTimeLeft > 0 && (
        <div className="fixed top-20 right-4 bg-red-600 text-white p-4 rounded shadow-lg w-64 z-50">
          <p className="font-semibold">⚠️ Inactivity Detected!</p>
          <p>Session expiring in: {inactivityTimeLeft}s</p>
          <p className="text-sm mt-1">Move mouse or press a key to reset.</p>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
