import React from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import Register from "./components/Register";
import Login from "./components/Login";
import Profile from "./components/Profile";
import ProtectedRoute from "./components/ProtectedRoute";
import Dashboard from "./pages/Dashboard";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  return (
    <BrowserRouter>
      <nav className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex h-16 items-center justify-between">
            <div className="flex items-center gap-6">
              <span className="text-xl font-bold text-indigo-700">JWT Demo</span>
              <div className="hidden md:flex items-center gap-4">
                <Link className="text-gray-700 hover:text-indigo-700" to="/register">Register</Link>
                <Link className="text-gray-700 hover:text-indigo-700" to="/login">Login</Link>
                <Link className="text-gray-700 hover:text-indigo-700" to="/profile">Profile</Link>
                <Link className="text-gray-700 hover:text-indigo-700" to="/dashboard">Dashboard</Link>
              </div>
            </div>
            <div className="md:hidden flex items-center gap-3">
              <Link className="text-gray-700 hover:text-indigo-700" to="/profile">Profile</Link>
            </div>
          </div>
        </div>
      </nav>

      <div className="min-h-[calc(100vh-4rem)] bg-gray-50">
        <Routes>
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
          <Route
            path="/profile"
            element={
              <ProtectedRoute>
                <Profile />
              </ProtectedRoute>
            }
          />
          <Route path="/dashboard" element={<Dashboard />} />
        </Routes>
      </div>

      {/* Toast notifications available everywhere */}
      <ToastContainer position="top-right" autoClose={3000} />
    </BrowserRouter>
  );
}

export default App;
